function images = take_pictures(fname)    camList = webcamlist;    n = size(camList,1);    images = {};    for i = 1:n        cam = webcam(i);        pause(5);        images{end+1} = snapshot(cam);%         subplot(1, n, i);%         imshow(images{end});    end        imwrite(images{1}, ['images/webcam stereo/facetime/' fname '.jpg']);    imwrite(images{2}, ['images/webcam stereo/logitech/' fname '.jpg']);